function moreInputInit(b,a){document.querySelector("#"+b+" .addmore-button").addEventListener("click",function(c){c.preventDefault();document.querySelector("#"+b).insertAdjacentHTML("beforeend",'<span class="addmore-span">'+a+"</span>")});document.querySelector("#"+b).addEventListener("click",function(d){var c=d.target||d.srcElement;if(!c.classList.contains("addmore-remove-button")){return}d.preventDefault();c.parentElement.remove()})};