function moreInputInit(d,a,c){if(!c){c=64}var b=document.getElementById(d).getElementsByClassName("addmore-span").length;document.querySelector("#"+d+" .addmore-button").addEventListener("click",function(f){f.preventDefault();if(b<c){document.querySelector("#"+d).insertAdjacentHTML("beforeend",'<span class="addmore-span">'+a+"</span>");b++}});document.querySelector("#"+d).addEventListener("click",function(g){var f=g.target||g.srcElement;if(!f.classList.contains("addmore-remove-button")){return}g.preventDefault();f.parentElement.remove();b--})};